/**
 * Get icon by name (case-insensitive)
 * Usage: getIconByName('home') or getIconByName('home-outline')
 */
export function getIconByName(name: string) {
  const key = name
    .toLowerCase()
    .replace(/-/g, '')
    .replace(/_/g, '');

  const found = Object.entries(EDUCATEPRO_ICONS).find(
    ([k]) => k.toLowerCase() === key
  );

  return found ? found[1] : null;
}

/**
 * List all available icon names
 */
export function getAvailableIconNames(): string[] {
  return Object.keys(EDUCATEPRO_ICONS);
}

export default EDUCATEPRO_ICONS;
